mapper.select:
  select
		cc.createtime
	from ccustomer cc
  	left join orderm om on om.ordermcustomer = cc.ccustomercustomer
  where om.ordermcompany = #{companyid}
  	and cc.ccustomercompany = #{companyid}
	and om.ordermstatue != '已删除'
    and om.ordermtime >= #{staTime} 
    and om.ordermtime <= #{endTime} 
  group by cc.createtime

select count(*) from ccustomer left outer join customer on ccustomer.ccustomercustomer = customer.customerid where ccustomer.ccustomercompany = #{ccustomer.ccustomercompany} and customer.customerstatue='启用'
and customer.customertype = #{ccustomer.customer.customertype}
and (customer.customercode like '%${ccustomer.customer.customercode}%' or customer.customername like '%${ccustomer.customer.customercode}%' or customer.customerphone like '%${ccustomer.customer.customercode}%' or customer.customershop like '%${ccustomer.customer.customercode}%' or customer.customercity like '%${ccustomer.customer.customercode}%' or customer.customerxian like '%${ccustomer.customer.customercode}%' or customer.customeraddress like '%${ccustomer.customer.customercode}%' or ccustomer.createtime like '%${ccustomer.customer.customercode}%' )
and ccustomer.creator = #{ccustomer.creator}

select * from (select A.*, ROWNUM RN from ( select ccustomer.ccustomerid, ccustomer.ccustomerdetail, ccustomer.createtime, customer.customerid,customer.customercode,customer.customername,customer.customerphone, customer.customerpsw,customer.customershop,customer.customercity,customer.customerxian, customer.customeraddress,customer.customertype,customer.customerlevel,customer.openid, customer.customerdetail,customer.customerstatue,customer.createtime as cuscreatetime,customer.updtime from ccustomer left outer join customer on ccustomer.ccustomercustomer = customer.customerid where ccustomer.ccustomercompany = #{ccustomer.ccustomercompany}
and customer.customertype = #{ccustomer.customer.customertype}
and (customer.customercode like '%${ccustomer.customer.customercode}%' or customer.customername like '%${ccustomer.customer.customercode}%' or customer.customerphone like '%${ccustomer.customer.customercode}%' or customer.customershop like '%${ccustomer.customer.customercode}%' or customer.customercity like '%${ccustomer.customer.customercode}%' or customer.customerxian like '%${ccustomer.customer.customercode}%' or customer.customeraddress like '%${ccustomer.customer.customercode}%' or ccustomer.createtime like '%${ccustomer.customer.customercode}%' )
and ccustomer.creator = #{ccustomer.creator}
order by ccustomer.ccustomerid desc ) A where ROWNUM <= (#{nowpage}*#{pagesize}) ) where RN > ((#{nowpage}-1)*#{pagesize})
