mapper.select:
  select * from 
	(select A.*, ROWNUM RN from (
  	select c.CUSTOMERSHOP from customer c 
  		left join ccustomer cc on cc.ccustomercustomer = c.customerid
		left join orderm om on om.ordermcustomer = c.customerid
	where om.ordermcompany = #{companyid} 
	and cc.ccustomercompany = #{companyid}
	and om.ordermtime >= #{staTime} 
	and om.ordermtime <= #{endTime} 
	and om.ordermstatue != '已删除'
	group by c.CUSTOMERSHOP
	) A where ROWNUM  <= 100) 
	where RN > 0

select distinct c.customerid as customerid,c.customershop as customershop,c.customername as customername, c.customerphone as customerphone,c.customercity as customercity,c.customerxian as customerxian, c.customeraddress as customeraddress,c.createtime as createtime,cc.createtime as cccreatetime, cc.ccustomerid as ccustomerid, count(om.ordermid) as odm_num, sum(om.ordermrightmoney) as odm_money from customer c left outer join ccustomer cc on c.customerid = cc.ccustomercustomer left outer join orderm om on om.ordermcustomer = c.customerid where cc.ccustomercompany = #{companyid} and om.ordermstatue != '已删除'
and om.ordermtime >= #{staTime}
and om.ordermtime <= #{endTime}
and ( cc.createtime like '%${staCusQuery}%' or c.customershop like '%${staCusQuery}%' )
group by c.customerid,c.customershop,c.customername,c.customerphone,c.customercity,c.customerxian,c.customeraddress,c.createtime,cc.createtime,cc.ccustomerid order by cccreatetime,customerid desc nulls last

select count(om.ordermid) as odm_num, sum(om.ordermrightmoney) as odm_money from customer c left outer join ccustomer cc on c.customerid = cc.ccustomercustomer left outer join orderm om on om.ordermcustomer = c.customerid where cc.ccustomercompany = #{companyid} and om.ordermstatue != '已删除'
and om.ordermtime >= #{staTime}
and om.ordermtime <= #{endTime}
and ( cc.createtime like '%${staCusQuery}%' or c.customershop like '%${staCusQuery}%' )

select count(distinct c.customerid) from customer c left outer join ccustomer cc on c.customerid = cc.ccustomercustomer left outer join orderm om on om.ordermcustomer = c.customerid where cc.ccustomercompany = #{companyid} and om.ordermstatue != '已删除'
and om.ordermtime >= #{staTime}
and om.ordermtime <= #{endTime}
and ( cc.createtime like '%${staCusQuery}%' or c.customershop like '%${staCusQuery}%' )

select * from (select A.*, ROWNUM RN from ( select distinct c.customerid as customerid,c.customershop as customershop,c.customername as customername, c.customerphone as customerphone,c.customercity as customercity,c.customerxian as customerxian, c.customeraddress as customeraddress,c.createtime as createtime,cc.createtime as cccreatetime, cc.ccustomerid as ccustomerid, count(om.ordermid) as odm_num, sum(om.ordermrightmoney) as odm_money from customer c left outer join ccustomer cc on c.customerid = cc.ccustomercustomer left outer join orderm om on om.ordermcustomer = c.customerid where cc.ccustomercompany = #{companyid} and om.ordermstatue != '已删除'
and om.ordermtime >= #{staTime}
and om.ordermtime <= #{endTime}
and ( cc.createtime like '%${staCusQuery}%' or c.customershop like '%${staCusQuery}%' )
group by c.customerid,c.customershop,c.customername,c.customerphone,c.customercity,c.customerxian,c.customeraddress,c.createtime,cc.createtime,cc.ccustomerid order by cccreatetime,customerid desc nulls last ) A where ROWNUM <= (#{nowpage}*#{pagesize}) ) where RN > ((#{nowpage}-1)*#{pagesize})

select c.customerid,c.customercode,c.customername,c.customerphone,c.customershop,c.customercity, c.customerxian,c.customeraddress,c.customertype,cc.ccustomerdetail as customerlevel, c.createtime,c.updtime,cc.createtime as customerpsw from customer c left join ccustomer cc on cc.ccustomercustomer = c.customerid where cc.ccustomercompany = #{ccustomercompany}
and c.customertype = #{customer.customertype}
and (c.customercode like '%${customer.customercode}%' or c.customername like '%${customer.customercode}%' or c.customerphone like '%${customer.customercode}%' or c.customershop like '%${customer.customercode}%' or c.customercity like '%${customer.customercode}%' or c.customerxian like '%${customer.customercode}%' or c.customeraddress like '%${customer.customercode}%' or cc.createtime like '%${customer.customercode}%' )
and cc.creator = #{creator}
