mapper.select:
select count(distinct goods.goodsid) from goods left join goodsclass gc on gc.goodsclassid = goods.goodsclass left join prices p on p.pricesgoods = goods.goodsid where goods.goodscompany = #{goodscompany} and p.pricesclass = '3' and p.creator = '启用'
and goods.goodsstatue = #{goodsstatue}
and (goods.GOODSCODE like '%${goodscode}%' or goods.GOODSNAME like '%${goodscode}%' or goods.GOODSUNITS like '%${goodscode}%' or goods.GOODSCLASS like '%${goodscode}%' or goods.GOODSSTATUE like '%${goodscode}%' or goods.CREATOR like '%${goodscode}%' or goods.UPDOR like '%${goodscode}%' or gc.goodsclassname like '%${goodscode}%')

select count(*) from goods left join goodsclass on goodsclass.goodsclassid = goods.goodsclass where goods.goodscompany = #{goodscompany}
and goods.goodsstatue = #{goodsstatue}
and (goods.GOODSCODE like '%${goodscode}%' or goods.GOODSNAME like '%${goodscode}%' or goods.GOODSUNITS like '%${goodscode}%' or goods.GOODSCLASS like '%${goodscode}%' or goods.GOODSSTATUE like '%${goodscode}%' or goods.CREATOR like '%${goodscode}%' or goods.UPDOR like '%${goodscode}%' or goodsclass.goodsclassname like '%${goodscode}%')

select * from (select A.*, ROWNUM RN from ( select goods.*, goodsclass.goodsclassname, goodsclass.goodsclassid from goods left join goodsclass on goodsclass.goodsclassid = goods.goodsclass where goods.goodscompany = #{goods.goodscompany}
and goods.goodsstatue = #{goods.goodsstatue}
and (goods.GOODSCODE like '%${goods.goodscode}%' or goods.GOODSNAME like '%${goods.goodscode}%' or goods.GOODSUNITS like '%${goods.goodscode}%' or goods.GOODSCLASS like '%${goods.goodscode}%' or goods.GOODSSTATUE like '%${goods.goodscode}%' or goods.CREATOR like '%${goods.goodscode}%' or goods.UPDOR like '%${goods.goodscode}%' or goodsclass.goodsclassname like '%${goods.goodscode}%')
order by goods.createtime,goods.goodsid desc ) A where ROWNUM <= (#{nowpage}*#{pagesize}) ) where RN > ((#{nowpage}-1)*#{pagesize})
