mapper.select:
  select count(*) from largecusprice lcp 
  	where lcp.largecuspricecompany = #{largecuspricecompany} 
	and lcp.largecuspricecustomer = #{largecuspricecustomer}
	and lcp.largecuspricegoods = #{largecuspricegoods}

select * from (select A.*, ROWNUM RN from ( select * from largecusprice lcp left join goods g on g.goodsid = lcp.largecuspricegoods left outer join goodsclass gc on g.goodsclass = gc.goodsclassid where lcp.largecuspricecompany = #{largecusprice.largecuspricecompany} and lcp.largecuspricecustomer = #{largecusprice.largecuspricecustomer}
and (lcp.largecuspriceprice like '%${largecusprice.largecuspriceprice}%' or lcp.largecuspricedetail like '%${largecusprice.largecuspriceprice}%' or lcp.largecuspricecreatetime like '%${largecusprice.largecuspriceprice}%' or lcp.largecuspricecreator like '%${largecusprice.largecuspriceprice}%' or lcp.largecuspriceunit like '%${largecusprice.largecuspriceprice}%' or lcp.largecuspriceprice2 like '%${largecusprice.largecuspriceprice}%' or lcp.largecuspriceunit2 like '%${largecusprice.largecuspriceprice}%' or g.GOODSCODE like '%${largecusprice.largecuspriceprice}%' or g.GOODSNAME like '%${largecusprice.largecuspriceprice}%' or gc.goodsclassname like '%${largecusprice.largecuspriceprice}%' )
order by lcp.largecuspricecreatetime desc ) A where ROWNUM <= (#{nowpage}*#{pagesize}) ) where RN > ((#{nowpage}-1)*#{pagesize})
