mapper.select:
select * from (select A.*, ROWNUM RN from ( select orderd.*, orderm.ordermid, orderm.ordermtime from orderd left join orderm on orderd.orderdorderm = orderm.ordermid where orderm.ordermcompany = #{companyid,jdbcType=VARCHAR}
and orderm.ordermtime >= #{staTime}
and orderm.ordermtime <= #{endTime}
and (orderd.orderdcode like '%${condition}%' or orderd.orderdname like '%${condition}%' or orderd.orderdunits like '%${condition}%' or orderd.orderdunit like '%${condition}%' or orderd.orderdnum like '%${condition}%' or orderd.orderdprice like '%${condition}%' or orderd.orderdmoney like '%${condition}%' or orderd.orderdrightmoney like '%${condition}%')
order by orderm.ordermtime desc ) A where ROWNUM <= (#{nowpage}*#{pagesize}) ) where RN > ((#{nowpage}-1)*#{pagesize})

select count(*) from orderd left join orderm on orderd.orderdorderm = orderm.ordermid where orderm.ordermcompany = #{companyid,jdbcType=VARCHAR}
and orderm.ordermtime >= #{staTime}
and orderm.ordermtime <= #{endTime}
and (orderd.orderdcode like '%${condition}%' or orderd.orderdname like '%${condition}%' or orderd.orderdunits like '%${condition}%' or orderd.orderdunit like '%${condition}%' or orderd.orderdnum like '%${condition}%' or orderd.orderdprice like '%${condition}%' or orderd.orderdmoney like '%${condition}%' or orderd.orderdrightmoney like '%${condition}%')
order by orderm.ordermtime desc

select sum(orderd.orderdnum) as numtotal, sum(orderd.orderdprice) as pricetotal, sum(orderd.orderdmoney) as moneytotal, sum(orderd.orderdrightmoney) as rightmoneytotal from orderd left join orderm on orderd.orderdorderm = orderm.ordermid where orderm.ordermcompany = #{companyid,jdbcType=VARCHAR}
and orderm.ordermtime >= #{staTime}
and orderm.ordermtime <= #{endTime}
and (orderd.orderdcode like '%${condition}%' or orderd.orderdname like '%${condition}%' or orderd.orderdunits like '%${condition}%' or orderd.orderdunit like '%${condition}%' or orderd.orderdnum like '%${condition}%' or orderd.orderdprice like '%${condition}%' or orderd.orderdmoney like '%${condition}%' or orderd.orderdrightmoney like '%${condition}%')
order by orderm.ordermtime desc

select orderd.*, orderm.ordermid, orderm.ordermtime from orderd left join orderm on orderd.orderdorderm = orderm.ordermid where orderm.ordermcompany = #{companyid,jdbcType=VARCHAR}
and orderm.ordermtime >= #{staTime}
and orderm.ordermtime <= #{endTime}
and (orderd.orderdcode like '%${condition}%' or orderd.orderdname like '%${condition}%' or orderd.orderdunits like '%${condition}%' or orderd.orderdunit like '%${condition}%' or orderd.orderdnum like '%${condition}%' or orderd.orderdprice like '%${condition}%' or orderd.orderdmoney like '%${condition}%' or orderd.orderdrightmoney like '%${condition}%')
order by orderm.ordermtime desc
