mapper.select:
 	select od.orderdcode,od.orderdtype,od.orderdunits,od.orderdprice from (select * from orderd where 
 	
 		orderdorderm=#{ordermid}
 	
	) od 
	group by od.orderdcode,od.orderdtype,od.orderdunits,od.orderdprice having count(*) >1

  	select g.goodsbrand from orderd od
		left join orderm om on om.ordermid = od.orderdorderm
		left join goods g on g.goodsid = od.orderdgoods
	where om.ordermcompany = #{companyid} 
	and om.ordermtime >= #{staTime} 
	and om.ordermtime <= #{endTime} 
	and om.ordermstatue != '已删除'
	and g.goodsbrand is not null
	group by g.goodsbrand

select * from orderd od where 

	ORDERDORDERM=#{ordermid}

order by orderdid desc

    select
	 d.*
	from orderd d
	left join orderm m
	on m.ordermid = d.orderdorderm
	where d.orderdtype = #{orderd.orderdtype}
	and m.ordermcustomer = #{orderd.orderm.ordermcustomer}
	and m.ORDERMTIME >= #{staTime}
	and m.ORDERMTIME <= #{endTime}

select * from (select A.*, ROWNUM RN from ( select od.*, om.ordermid, om.ordermtime from orderd od left join orderm om on od.orderdorderm = om.ordermid left join customer c on c.customerid = om.ordermcustomer left join ccustomer cc on cc.ccustomercustomer = om.ordermcustomer left join goods g on od.orderdgoods = g.goodsid where om.ordermcompany = #{companyid,jdbcType=VARCHAR} and cc.ccustomercompany = #{companyid,jdbcType=VARCHAR} and om.ordermstatue != '已删除'
and
c.customershop=#{cusName}
and
cc.createtime=#{empName}
and
g.goodsbrand=#{brandName}
and om.ordermtime >= #{staTime}
and om.ordermtime <= #{endTime}
and (od.orderdcode like '%${condition}%' or od.orderdname like '%${condition}%' or od.orderdunits like '%${condition}%' or od.orderdunit like '%${condition}%' or od.orderdnum like '%${condition}%' or od.orderdprice like '%${condition}%' or od.orderdmoney like '%${condition}%' or od.orderdrightmoney like '%${condition}%')
order by om.ordermtime desc ) A where ROWNUM <= (#{nowpage}*#{pagesize}) ) where RN > ((#{nowpage}-1)*#{pagesize})

select count(*) from orderd od left join orderm om on od.orderdorderm = om.ordermid left join customer c on c.customerid = om.ordermcustomer left join ccustomer cc on cc.ccustomercustomer = c.customerid left join goods g on od.orderdgoods = g.goodsid where om.ordermcompany = #{companyid,jdbcType=VARCHAR} and cc.ccustomercompany = #{companyid,jdbcType=VARCHAR} and om.ordermstatue != '已删除'
and
c.customershop=#{cusName}
and
cc.createtime=#{empName}
and
g.goodsbrand=#{brandName}
and om.ordermtime >= #{staTime}
and om.ordermtime <= #{endTime}
and (od.orderdcode like '%${condition}%' or od.orderdname like '%${condition}%' or od.orderdunits like '%${condition}%' or od.orderdunit like '%${condition}%' or od.orderdnum like '%${condition}%' or od.orderdprice like '%${condition}%' or od.orderdmoney like '%${condition}%' or od.orderdrightmoney like '%${condition}%')

select sum(od.orderdnum) as numtotal, sum(od.orderdmoney) as moneytotal, sum(od.orderdrightmoney) as rightmoneytotal, sum(od.orderdweight) as weighttotal from orderd od left join orderm om on od.orderdorderm = om.ordermid left join customer c on c.customerid = om.ordermcustomer left join ccustomer cc on cc.ccustomercustomer = om.ordermcustomer left join goods g on od.orderdgoods = g.goodsid where om.ordermcompany = '1' and cc.ccustomercompany = '1' and om.ordermstatue != '已删除'
and
c.customershop=#{cusName}
and
cc.createtime=#{empName}
and
g.goodsbrand=#{brandName}
and om.ordermtime >= #{staTime}
and om.ordermtime <= #{endTime}
and (od.orderdcode like '%${condition}%' or od.orderdname like '%${condition}%' or od.orderdunits like '%${condition}%' or od.orderdunit like '%${condition}%' or od.orderdnum like '%${condition}%' or od.orderdprice like '%${condition}%' or od.orderdmoney like '%${condition}%' or od.orderdrightmoney like '%${condition}%')

select od.*, om.ordermid, om.ordermtime from orderd od left join orderm om on od.orderdorderm = om.ordermid left join customer c on c.customerid = om.ordermcustomer left join ccustomer cc on cc.ccustomercustomer = c.customerid left join goods g on od.orderdgoods = g.goodsid where om.ordermcompany = #{companyid,jdbcType=VARCHAR} and om.ordermstatue != '已删除'
and
c.customershop=#{cusName}
and
cc.createtime=#{empName}
and
g.goodsbrand=#{brandName}
and om.ordermtime >= #{staTime}
and om.ordermtime <= #{endTime}
and (od.orderdcode like '%${condition}%' or od.orderdname like '%${condition}%' or od.orderdunits like '%${condition}%' or od.orderdunit like '%${condition}%' or od.orderdnum like '%${condition}%' or od.orderdprice like '%${condition}%' or od.orderdmoney like '%${condition}%' or od.orderdrightmoney like '%${condition}%')
order by om.ordermtime desc
