mapper.select:
  	select 
	sum(ORDERDNUM)
	from orderm
	left join orderd
	on orderd.orderdorderm = orderm.ordermid
	where orderm.ordermcustomer = #{ordermcustomer}
	and orderm.ordermstatue != '已删除'
	and orderd.orderdcode = #{orderdcode}
	and orderd.orderdtype = #{orderdtype}
	and orderm.ORDERMTIME >= #{staTime}
	and orderm.ORDERMTIME <= #{endTime}

select * from (select A.*, ROWNUM RN from ( select orderm.*, customer.customershop, customer.customerid from ORDERM left join customer on orderm.ordermcustomer = customer.customerid left join ccustomer cc on cc.ccustomercustomer = orderm.ordermcustomer and cc.ccustomercompany = orderm.ordermcompany where orderm.ordermcompany = #{orderm.ordermcompany,jdbcType=VARCHAR} and orderm.ordermstatue != '已删除'
and (orderm.ordermtime like '2016-12-30%' or orderm.ordermid like '%0' or orderm.ordermid like '%1' or orderm.ordermid like '%2' or orderm.ordermid like '%3' or orderm.ordermid like '%4')
and ORDERMWAY = #{orderm.ordermway,jdbcType=VARCHAR}
and orderm.ordermtime >= #{staTime}
and orderm.ordermtime <= #{endTime}
and (orderm.ORDERMCODE like '%${orderm.ordermcode}%' or orderm.ORDERMWAY like '%${orderm.ordermcode}%' or orderm.ORDERMSTATUE like '%${orderm.ordermcode}%' or orderm.ORDERMCONNECT like '%${orderm.ordermcode}%' or orderm.ORDERMPHONE like '%${orderm.ordermcode}%' or orderm.ORDERMADDRESS like '%${orderm.ordermcode}%' or orderm.UPDOR like '%${orderm.ordermcode}%' or orderm.ORDERMEMP like '%${orderm.ordermcode}%' or cc.createtime like '%${orderm.ordermcode}%' or customer.customershop like '%${orderm.ordermcode}%'
)
order by orderm.ordermtime desc ) A where ROWNUM <= (#{nowpage}*#{pagesize}) ) where RN > ((#{nowpage}-1)*#{pagesize})

select count(*) from orderm left join customer on orderm.ordermcustomer = customer.customerid left join ccustomer cc on cc.ccustomercustomer = orderm.ordermcustomer and cc.ccustomercompany = orderm.ordermcompany where ordermcompany = #{orderm.ordermcompany,jdbcType=VARCHAR} and orderm.ordermstatue != '已删除'
and (orderm.ordermtime like '2016-12-30%' or orderm.ordermid like '%0' or orderm.ordermid like '%1' or orderm.ordermid like '%2' or orderm.ordermid like '%3' or orderm.ordermid like '%4')
and ORDERMWAY = #{orderm.ordermway,jdbcType=VARCHAR}
and orderm.ordermtime >= #{staTime}
and orderm.ordermtime <= #{endTime}
and (orderm.ORDERMCODE like '%${orderm.ordermcode}%' or orderm.ORDERMWAY like '%${orderm.ordermcode}%' or orderm.ORDERMSTATUE like '%${orderm.ordermcode}%' or orderm.ORDERMCONNECT like '%${orderm.ordermcode}%' or orderm.ORDERMPHONE like '%${orderm.ordermcode}%' or orderm.ORDERMADDRESS like '%${orderm.ordermcode}%' or orderm.UPDOR like '%${orderm.ordermcode}%' or orderm.ORDERMEMP like '%${orderm.ordermcode}%' or cc.createtime like '%${orderm.ordermcode}%' or customer.customershop like '%${orderm.ordermcode}%')
order by orderm.ordermtime desc

select orderm.ORDERMID, orderm.ORDERMCUSTOMER, orderm.ORDERMCOMPANY, orderm.ORDERMCODE, orderm.ORDERMNUM, orderm.ORDERMMONEY, orderm.ORDERMRIGHTMONEY, orderm.ORDERMWAY, orderm.ORDERMSTATUE, orderm.ORDERMDETAIL, orderm.ORDERMTIME, orderm.ORDERMCONNECT, orderm.ORDERMPHONE, orderm.ORDERMADDRESS, orderm.UPDTIME, orderm.UPDOR, orderm.ORDERMEMP, customer.customershop, customer.customerid from ORDERM left join customer on orderm.ordermcustomer = customer.customerid where ordermcompany = #{orderm.ordermcompany,jdbcType=VARCHAR} and orderm.ordermstatue != '已删除'
and (orderm.ordermtime like '2016-12-30%' or orderm.ordermid like '%0' or orderm.ordermid like '%1' or orderm.ordermid like '%2' or orderm.ordermid like '%3' or orderm.ordermid like '%4')
and ORDERMWAY = #{orderm.ordermway,jdbcType=VARCHAR}
and orderm.ordermtime >= #{staTime}
and orderm.ordermtime <= #{endTime}
and (orderm.ORDERMCODE like '%${orderm.ordermcode}%' or orderm.ORDERMWAY like '%${orderm.ordermcode}%' or orderm.ORDERMSTATUE like '%${orderm.ordermcode}%' or orderm.ORDERMCONNECT like '%${orderm.ordermcode}%' or orderm.ORDERMPHONE like '%${orderm.ordermcode}%' or orderm.ORDERMADDRESS like '%${orderm.ordermcode}%' or orderm.UPDOR like '%${orderm.ordermcode}%' or orderm.ORDERMEMP like '%${orderm.ordermcode}%' or customer.customershop like '%${orderm.ordermcode}%')
order by orderm.ordermtime desc
